from proxyclient import base

class Central(base.Resource): pass

class CentralManager(base.Manager):
    resource_class = Central

    def server_get_by_key(self, project, **kwargs):
        return self._create('/servers/compute/%s' % base.getid(project), kwargs,
                          'server', return_raw=True)

    def server_get_by_id(self, sid, did):
        return self._get('/servers/compute/%s/%s' % (sid, did), 'servers')

    def gateway_get(self, hostname):
        body = {'hostname': hostname}
        return self._create('/os-gateway', body, 'gateway', return_raw=True)
