from proxyclient import base

class Gateway(base.Resource):
    """A Firewall"""
    pass

class GatewayManager(base.Manager):
    resource_class = Gateway

    def get(self, instance_id):
        return self._list("/os-gateways/gateway/%s" % base.getid(
            instance_id), "gateways")

    def list(self, project):
        return self._list("/os-gateways/%s" % base.getid(project), "gateways")
